import React, { Component } from 'react'
import { MapContainer, TileLayer, Marker, Popup, GeoJSON  } from 'react-leaflet'


export default class MainMap extends Component {
    state = {
        lat: 37.7749,
        lng: -122.4194,
        zoom: 13,
    }
    render() {
        const marker = () => {
            if(this.props.map.point) {
                return <Marker position={this.props.map.point} />;
            }
        };

        const data = () => {
            if(this.props.map.data.length > 0) {
                const json = this.props.map.data;
                return <GeoJSON data={json} />
            }
        }
        return (
            this.props.streetlights ?
                <MapContainer 
                   center={[this.state.lat, this.state.lng]} 
                   zoom={this.state.zoom} 
                   style={{ width: '100%', height: '900px'}}
                >
                <TileLayer
                  attribution='&copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                  url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                 />

                 <GeoJSON data={this.props.streetlights} />
               </MapContainer>
                 :
                 'Data is loading...'
         )
    }
}
