import React, { Component } from 'react'
import { MapContainer, CircleMarker, TileLayer, Marker, Popup, GeoJSON, Tooltip, AttributionControl  } from 'react-leaflet'
import streetlights from '../streetlights.json';
import MarkerClusterGroup from 'react-leaflet-markercluster';

export default class MainMap extends Component {
    state = {
        lat: 37.7749,
        lng: -122.4194,
        zoom: 13,
    }
    constructor(props, context) {
        super(props, context);

        this.state = {
            lat: 38.0293, 
            lng: -78.4767,
            zoom: 13,
            data: []
        };

        this.geoJsonLayer = React.createRef();
    }
    showPreview = (place) => {
        // show place's description
    }
    fixtureStyle = {
        fillColor: "red",
        fillOpacity: 1,
        color: "black",
        weight: 2,
    };
    changeCountryColor = (event) => {
        event.target.setStyle({
          color: "green",
          fillColor: this.state.color,
          fillOpacity: 1,
        });
    };

    onEachCountry = (fixture, layer) => {
        const lampCD = fixture.properties.LAMP_CD;
        console.log(lampCD);
        layer.bindPopup(lampCD);

        layer.options.fillOpacity = Math.random(); //0-1 (0.1, 0.2, 0.3)
        // const colorIndex = Math.floor(Math.random() * this.colors.length);
        // layer.options.fillColor = this.colors[colorIndex]; //0

        layer.on({
          click: this.changeCountryColor,
        });
    };

    render() {
        return(
            this.props.streetlights ?

            <MapContainer
                   center={[this.state.lat, this.state.lng]} 
                   zoom={this.state.zoom} 
                   style={{ width: '100%', height: '900px'}}
                >
                <TileLayer
                    url='https://cartodb-basemaps-{s}.global.ssl.fastly.net/light_all/{z}/{x}/{y}.png'
                />
                          <AttributionControl position="bottomright" prefix={false} />

                <MarkerClusterGroup
                        spiderfyDistanceMultiplier={1}
                        showCoverageOnHover={false}
                >   
                {
                        streetlights.features.map((dataItem, k) => {
                        //let { coordinates, company, url, loc } = dataItem;
                        let coordinates = dataItem.geometry.coordinates;
                        let fixtureStyle = dataItem.properties.FIXTURE_ST;
                        return (
                            <CircleMarker
                                key={k}
                                center={[coordinates[0], coordinates[1]]}
                                position={[coordinates[0], coordinates[1]]}
                            >
                            <Tooltip direction="right" offset={[-8, -2]} opacity={1}>
                                <span>{fixtureStyle}</span>
                            </Tooltip>
                            </CircleMarker>
                        );
                })
                }
                </MarkerClusterGroup>

            </MapContainer>
        :
            'Data is loading...'
        )
    }

}

