import React, { Component } from 'react'
import { MapContainer, TileLayer, Marker, Popup, GeoJSON  } from 'react-leaflet'


export default class MainMap extends Component {
    state = {
        lat: 37.7749,
        lng: -122.4194,
        zoom: 13,
    }
    showPreview = (place) => {
        // show place's description
    }
    render() {
        return(
            this.props.streetlights ?

            <MapContainer 
                   center={[this.state.lat, this.state.lng]} 
                   zoom={this.state.zoom} 
                   style={{ width: '100%', height: '900px'}}
                >
                console.log(this.props.streetlights);
                {this.props.streetlights.features.properties.map((light) => (
                    <Marker
                        key={light.FIXTURE_ST}
                        position={light.geometry.coordinates} // ðŸ‘ˆ
                        eventHandlers={{ click: () => showPreview(place) }}
                    >
            {/* show place's title on hover the marker */}
            <Tooltip>{place.title}</Tooltip> 
          </Marker>
        ))}
                <TileLayer
                  attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                  url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />


            </MapContainer>
        :
            'Data is loading...'
        )
    }

}

